import { creactSlice } from '@reduxjs/toolkit';

const uiSlice = creactSlice({
  name: 'ui',

  initialState: {
    cartIsVisible: false,
  },

  reducer: {
    toggle(state) {
      state.cartIsVisible = !state.cartIsVisible;
    },
  },
});

export const uiActions = uiSlice.actions;

export default uiSlice.reducer;
